CREATE OR REPLACE FUNCTION fnc_getReadableSize(p_blob BLOB) RETURN VARCHAR2 IS\n    v_size NUMBER;\n    v_readable_size VARCHAR2(20);\nBEGIN\n    -- Get the size of the BLOB\n    v_size := DBMS_LOB.GETLENGTH(p_blob);\n\n    -- Convert the size into a human-readable format\n    IF v_size < 1024 THEN\n        v_readable_size := TO_CHAR(v_size) || ' Bytes';\n    ELSIF v_size < 1048576 THEN\n        v_readable_size := TO_CHAR(ROUND(v_size / 1024, 2)) || ' KB';\n    ELSIF v_size < 1073741824 THEN\n        v_readable_size := TO_CHAR(ROUND(v_size / 1048576, 2)) || ' MB';\n    ELSE\n        v_readable_size := TO_CHAR(ROUND(v_size / 1073741824, 2)) || ' GB';\n    END IF;\n\n    RETURN v_readable_size;\nEND;\n/