-- Define a record type for user data\nCREATE OR REPLACE TYPE User_Record AS OBJECT (\n    Name VARCHAR2(50),\n    Surname VARCHAR2(50),\n    Dt_Birth DATE\n);\n\n-- Define a collection type based on the record type\nCREATE OR REPLACE TYPE User_Table AS TABLE OF User_Record;\n\n-- Create the stored procedure to insert multiple users\nCREATE OR REPLACE PROCEDURE Insert_Users(p_users IN User_Table) AS\nBEGIN\n    FOR i IN 1 .. p_users.COUNT LOOP\n        INSERT INTO "USER" (Name, Surname, Dt_Birth)\n        VALUES (p_users(i).Name, p_users(i).Surname, p_users(i).Dt_Birth);\n    END LOOP;\nEND;\n/