CREATE PROCEDURE UpdateCountryData\nAS\nBEGIN\n    -- Declare variables to hold the values from the Translations table\n    DECLARE @From NVARCHAR(255);\n    DECLARE @To NVARCHAR(255);\n\n    -- Cursor to iterate through each row in the Translations table\n    DECLARE translationCursor CURSOR FOR\n    SELECT [From], [To]\n    FROM Translations;\n\n    -- Open the cursor\n    OPEN translationCursor;\n\n    -- Fetch the first row\n    FETCH NEXT FROM translationCursor INTO @From, @To;\n\n    -- Loop through each row in the cursor\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n        -- Update the Data table based on the current row from the Translations table\n        UPDATE Data\n        SET Country = @To\n        WHERE Country = @From;\n\n        -- Fetch the next row\n        FETCH NEXT FROM translationCursor INTO @From, @To;\n    END;\n\n    -- Close and deallocate the cursor\n    CLOSE translationCursor;\n    DEALLOCATE translationCursor;\nEND;