-- Assuming the input list of articles is [A, B, ..., N]\n-- For this example, I'll use [1, 2, 3] as the input list\n\nSELECT DISTINCT c1.artTwo AS compatible_article\nFROM Compatible c1\nWHERE c1.artOne IN (1, 2, 3) OR c1.artTwo IN (1, 2, 3)\nAND NOT EXISTS (\n    SELECT 1\n    FROM Compatible c2\n    WHERE (c2.artOne = c1.artTwo AND c2.artTwo NOT IN (1, 2, 3))\n       OR (c2.artTwo = c1.artTwo AND c2.artOne NOT IN (1, 2, 3))\n)\nUNION\nSELECT DISTINCT c2.artOne AS compatible_article\nFROM Compatible c2\nWHERE c2.artTwo IN (1, 2, 3) OR c2.artOne IN (1, 2, 3)\nAND NOT EXISTS (\n    SELECT 1\n    FROM Compatible c1\n    WHERE (c1.artOne = c2.artOne AND c1.artTwo NOT IN (1, 2, 3))\n       OR (c1.artTwo = c2.artOne AND c1.artOne NOT IN (1, 2, 3))\n);