SELECT \n    SUM(OrderDetail_table.Price * OrderDetail_table.Quantity) AS totalPrice,\n    Item_table.GLacct \nFROM \n    OrderDetail_table \n    LEFT OUTER JOIN Order_table ON OrderDetail_table.OrderID = Order_table.OrderID \n    LEFT OUTER JOIN Item_table ON Item_table.ItemID = OrderDetail_table.ItemID \nWHERE \n    Order_table.InvoiceDate = #01/31/2009# \nGROUP BY \n    Item_table.GLacct;