-- Ignore rows causing primary key violation\nINSERT IGNORE INTO CommReceipt (CR_Key, Column1, Column2)\nSELECT CR_Key, Column1, Column2 FROM StagingTable;\n\n-- OR update existing rows if a primary key violation occurs\nINSERT INTO CommReceipt (CR_Key, Column1, Column2)\nSELECT CR_Key, Column1, Column2 FROM StagingTable\nON DUPLICATE KEY UPDATE\nColumn1 = VALUES(Column1),\nColumn2 = VALUES(Column2);