-- Create the sample table\nCREATE TABLE Responses (\n    Response NVARCHAR(MAX)\n);\n\n-- Insert sample data\nINSERT INTO Responses (Response) VALUES\n('Red and White'),\n('green'),\n('White and blue'),\n('Blue'),\n('Dark blue');\n\n-- SQL query to count word frequency\nWITH TokenizedWords AS (\n    SELECT \n        TRIM(value) AS Word\n    FROM \n        Responses\n    CROSS APPLY \n        STRING_SPLIT(Response, ' ')\n),\nFilteredWords AS (\n    SELECT \n        Word\n    FROM \n        TokenizedWords\n    WHERE \n        LEN(Word) > 0\n)\nSELECT \n    Word,\n    COUNT(*) AS Frequency\nFROM \n    FilteredWords\nGROUP BY \n    Word\nORDER BY \n    Frequency DESC, Word;