-- Create a symmetric key for encryption\nCREATE SYMMETRIC KEY MySymmetricKey\nWITH ALGORITHM = AES_256\nENCRYPTION BY PASSWORD = 'YourStrongPassword';\n\n-- Encrypt data using the symmetric key\nOPEN SYMMETRIC KEY MySymmetricKey\nDECRYPTION BY PASSWORD = 'YourStrongPassword';\n\nDECLARE @EncryptedData VARBINARY(MAX);\nSET @EncryptedData = ENCRYPTBYKEY(KEY_GUID('MySymmetricKey'), 'Sensitive Data');\n\n-- Store the encrypted data\nINSERT INTO YourTable (EncryptedColumn) VALUES (@EncryptedData);\n\n-- Log the action\nINSERT INTO AuditLog (Event, EventTime, UserName)\nVALUES ('Data Encrypted', GETDATE(), SYSTEM_USER);\n\n-- Decrypt data using the symmetric key\nDECLARE @DecryptedData NVARCHAR(MAX);\nSET @DecryptedData = CONVERT(NVARCHAR(MAX), DECRYPTBYKEY(@EncryptedData));\n\n-- Close the symmetric key\nCLOSE SYMMETRIC KEY MySymmetricKey;