DECLARE\n    v_start_date DATE := TO_DATE('06/01/2009', 'MM/DD/YYYY');\n    v_end_date DATE := TO_DATE('06/19/2009', 'MM/DD/YYYY');\n    v_date SYS_REFCURSOR;\n    v_generated_date DATE;\nBEGIN\n    -- Generate the date range\n    v_date := generate_date_range(v_start_date, v_end_date);\n    \n    -- Loop through each generated date and produce the report\n    LOOP\n        FETCH v_date INTO v_generated_date;\n        EXIT WHEN v_date%NOTFOUND;\n        \n        -- Query to get the count of options for each date\n        FOR rec IN (SELECT option, COUNT(*) AS count\n                    FROM data\n                    WHERE v_generated_date BETWEEN start_date AND end_date\n                    GROUP BY option)\n        LOOP\n            DBMS_OUTPUT.PUT_LINE(TO_CHAR(v_generated_date, 'MM/DD/YYYY') || ' ' || rec.option || ' ' || rec.count);\n        END LOOP;\n    END LOOP;\n    \n    -- Close the cursor\n    CLOSE v_date;\nEND;\n/