CREATE OR REPLACE TRIGGER LabEx5_1\nAFTER INSERT ON concessions_sold\nFOR EACH ROW\nBEGIN\n    -- Update the quantity in the inventory table\n    UPDATE inventory i\n    SET i.quantity = i.quantity - :new.quantity  -- Assuming you want to subtract the sold quantity\n    WHERE i.inventory_id = (SELECT c.inventory_id\n                            FROM concession c\n                            WHERE c.concession_id = :new.concession_id);\nEND LabEx5_1;\n/