CREATE PROCEDURE InsertParentAndChild\n    @ParentTimestamp DATETIME,\n    @ChildData NVARCHAR(100)\nAS\nBEGIN\n    -- Insert into the parent table\n    INSERT INTO PARENT_TABLE (TIMESTAMP)\n    VALUES (@ParentTimestamp);\n    \n    -- Retrieve the auto-incremented ID of the newly inserted parent row\n    DECLARE @NewParentID INT;\n    SET @NewParentID = SCOPE_IDENTITY();\n    \n    -- Insert into the child table using the retrieved parent ID\n    INSERT INTO CHILD_TABLE (ParentID, Data)\n    VALUES (@NewParentID, @ChildData);\nEND