-- Example to gather column statistics\nBEGIN\n  DBMS_STATS.GATHER_TABLE_STATS(\n    ownname => 'SCHEMA_NAME',\n    tabname => 'TABLE_NAME',\n    method_opt => 'FOR ALL COLUMNS SIZE 1'\n  );\nEND;\n/\n\n-- Query to retrieve the min and max value from the statistics\nSELECT\n  column_name,\n  TO_NUMBER(DBMS_STATS.CONVERT_RAW_VALUE(low_value)) AS min_value,\n  TO_NUMBER(DBMS_STATS.CONVERT_RAW_VALUE(high_value)) AS max_value\nFROM\n  user_tab_col_statistics\nWHERE\n  table_name = 'TABLE_NAME'\n  AND column_name = 'COLUMN_NAME';