WITH TimeInSeconds AS (\n    SELECT\n        runtime,\n        DATEPART(HOUR, runtime) * 3600 +\n        DATEPART(MINUTE, runtime) * 60 +\n        DATEPART(SECOND, runtime) AS TotalSeconds\n    FROM YourTableName\n)\nSELECT \n    CAST(SUM(TotalSeconds) / 3600 AS VARCHAR) + ':' +\n    RIGHT('0' + CAST((SUM(TotalSeconds) % 3600) / 60 AS VARCHAR), 2) + ':' +\n    RIGHT('0' + CAST(SUM(TotalSeconds) % 60 AS VARCHAR), 2) AS TotalRuntime\nFROM TimeInSeconds;