USE [Mattercentre_dev];\nGO\n\nSET ANSI_NULLS ON;\nGO\nSET QUOTED_IDENTIFIER OFF;\nGO\n\nALTER PROCEDURE [dbo].[UDSPRBHPRIMBUSTYPE]\nWITH EXECUTE AS 'ReadUser'\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @SERVERNAME nvarchar(30);\n    DECLARE @DBASE nvarchar(30);\n    DECLARE @SQL nvarchar(2000);\n\n    SET @SERVERNAME = (SELECT Convert(nvarchar, spData) FROM dbSpecificData WHERE spLookup = 'CMSSERVER');\n    SET @DBASE = (SELECT Convert(nvarchar, spData) FROM dbSpecificData WHERE spLookup = 'CMSDBNAME');\n\n    SET @SQL = 'SELECT null as Code, ''(not specified)'' as Description UNION SELECT clnt_cat_code as Code, clnt_cat_desc as Description FROM ' \n                + @SERVERNAME + '.' + @DBASE + '.dbo.hbl_clnt_cat WHERE inactive = ''N'' ORDER BY Description';\n\n    EXECUTE sp_executesql @SQL;\nEND;\nGO