WITH ConsecutiveWinners AS (\n    SELECT\n        *,\n        ROW_NUMBER() OVER (ORDER BY created) - \n        ROW_NUMBER() OVER (PARTITION BY winner ORDER BY created) AS grp\n    FROM\n        your_table\n    WHERE\n        winner = 1\n),\nGroupedWinners AS (\n    SELECT\n        grp,\n        MIN(created) AS first_created,\n        MAX(created) AS last_created,\n        COUNT(*) AS consecutive_winners,\n        SUM(profit) AS total_profit\n    FROM\n        ConsecutiveWinners\n    GROUP BY\n        grp\n)\nSELECT\n    consecutive_winners,\n    first_created,\n    last_created,\n    total_profit\nFROM\n    GroupedWinners\nORDER BY\n    consecutive_winners DESC\nLIMIT 1;