DECLARE @tableName NVARCHAR(128) = 'YourTableName'\nDECLARE @columnName NVARCHAR(128) = 'YourColumnName'\nDECLARE @sql NVARCHAR(MAX)\n\nSELECT @sql = 'ALTER TABLE ' + QUOTENAME(@tableName) + ' DROP CONSTRAINT ' + QUOTENAME(fk.name)\nFROM sys.foreign_keys AS fk\nJOIN sys.foreign_key_columns AS fkc ON fk.object_id = fkc.constraint_object_id\nJOIN sys.columns AS c ON c.object_id = fkc.parent_object_id AND c.column_id = fkc.parent_column_id\nWHERE fk.parent_object_id = OBJECT_ID(@tableName) AND c.name = @columnName\n\nEXEC sp_executesql @sql