CREATE OR REPLACE FUNCTION str_to_number_list(p_str IN VARCHAR2) RETURN t_number_list AS\n        l_number_list t_number_list := t_number_list();\n        l_str VARCHAR2(32767) := p_str || ',';\n        l_comma_pos PLS_INTEGER;\n    BEGIN\n        LOOP\n            l_comma_pos := INSTR(l_str, ',');\n            EXIT WHEN l_comma_pos = 0;\n            l_number_list.EXTEND;\n            l_number_list(l_number_list.COUNT) := TO_NUMBER(TRIM(SUBSTR(l_str, 1, l_comma_pos - 1)));\n            l_str := SUBSTR(l_str, l_comma_pos + 1);\n        END LOOP;\n        RETURN l_number_list;\n    END;\n    /