-- Step 1: Create a temporary table\nCREATE TABLE #temp_bank_account (\n    bank VARCHAR(255),\n    account VARCHAR(255)\n);\n\n-- Step 2: Insert the list of bank-account pairs into the temporary table\n-- Assuming the client provides the list as follows\nINSERT INTO #temp_bank_account (bank, account) VALUES ('Bank1', 'Account1');\nINSERT INTO #temp_bank_account (bank, account) VALUES ('Bank2', 'Account2');\n-- Add more pairs as needed...\n\n-- Step 3: Perform the query using join\nSELECT \n    b.date, \n    SUM(b.amount) AS amount \nFROM \n    balances b\nJOIN \n    #temp_bank_account tba \nON \n    b.bank = tba.bank AND b.account = tba.account\nGROUP BY \n    b.date;\n\n-- Step 4: Drop the temporary table if no longer needed\nDROP TABLE #temp_bank_account;