ALTER FUNCTION [dbo].[FullTextSearch]\n(\n    @Columns nvarchar(100), \n    @SearchPhrase nvarchar(100)\n)\nRETURNS TABLE\nAS\nRETURN \n(\n    SELECT * \n    FROM projects AS P \n    INNER JOIN \n    (\n        SELECT \n            CASE \n                WHEN @Columns = 'Title' THEN (SELECT * FROM containstable(PROJECTS, Title, @SearchPhrase))\n                WHEN @Columns = 'Project_Details' THEN (SELECT * FROM containstable(PROJECTS, Project_Details, @SearchPhrase))\n                WHEN @Columns = 'Contact_Info' THEN (SELECT * FROM containstable(PROJECTS, Contact_Info, @SearchPhrase))\n                WHEN @Columns = 'Project_Description' THEN (SELECT * FROM containstable(PROJECTS, Project_Description, @SearchPhrase))\n                ELSE (SELECT * FROM containstable(PROJECTS, (Title, Project_Details, Contact_Info, Project_Description), @SearchPhrase))\n            END AS K\n    ) AS K\n    ON P.project_id = K.[KEY]\n);