-- Example of creating and querying with trigrams (3-grams)\n   -- First, create a function to generate trigrams\n   CREATE FUNCTION dbo.Trigrams(@input NVARCHAR(255))\n   RETURNS TABLE\n   AS\n   RETURN\n   (\n       WITH L0 AS(SELECT 1 AS c UNION ALL SELECT 1),\n       L1 AS(SELECT 1 AS c FROM L0 AS A, L0 AS B),\n       L2 AS(SELECT 1 AS c FROM L1 AS A, L1 AS B),\n       L3 AS(SELECT 1 AS c FROM L2 AS A, L2 AS B),\n       Numbers AS(SELECT ROW_NUMBER() OVER(ORDER BY (SELECT NULL)) AS n FROM L3)\n       SELECT SUBSTRING(@input, n, 3) AS trigram\n       FROM Numbers\n       WHERE n <= LEN(@input) - 2\n   );\n\n   -- Then use the function to find similar words\n   SELECT a.your_column\n   FROM your_table a\n   CROSS APPLY dbo.Trigrams(a.your_column) t1\n   CROSS APPLY dbo.Trigrams('fireman') t2\n   WHERE t1.trigram = t2.trigram\n   GROUP BY a.your_column\n   ORDER BY COUNT(*) DESC;