CREATE TABLE ChecklistMonths (\n    ChecklistMonthID INT PRIMARY KEY, -- 1 to 12 for each month\n    MonthName VARCHAR(20) NOT NULL -- Optional: 'January', 'February', etc.\n);\n\nCREATE TABLE ChecklistQuestions (\n    ChecklistQuestionID INT PRIMARY KEY, \n    QuestionText VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE ChecklistMonthQuestions (\n    ChecklistMonth INT NOT NULL, \n    ChecklistQuestionID INT NOT NULL,\n    FOREIGN KEY (ChecklistMonth) REFERENCES ChecklistMonths(ChecklistMonthID),\n    FOREIGN KEY (ChecklistQuestionID) REFERENCES ChecklistQuestions(ChecklistQuestionID)\n);