CREATE OR REPLACE FUNCTION MY_FUNC(PXOBJCLASS VARCHAR2)\nRETURN MY_OBJ_TABLE PIPELINED IS\n    TYPE ref1 IS REF CURSOR;\n    Cur1 ref1;\n    out_rec my_object := my_object(NULL, NULL, NULL);\nBEGIN\n    OPEN Cur1 FOR 'SELECT PYID, PXINSNAME, PZINSKEY \n                   FROM PC_WORK \n                   WHERE PXOBJCLASS = :1' USING PXOBJCLASS;\n    LOOP\n        FETCH Cur1 INTO out_rec.COL1, out_rec.COL2, out_rec.COL3;\n        EXIT WHEN Cur1%NOTFOUND;\n        PIPE ROW(out_rec);\n    END LOOP;\n    CLOSE Cur1;\n    RETURN;\nEND MY_FUNC;