-- Enable xp_cmdshell if it is not already enabled\nEXEC sp_configure 'show advanced options', 1;\nRECONFIGURE;\nEXEC sp_configure 'xp_cmdshell', 1;\nRECONFIGURE;\n\n-- Create a temporary table to hold the file content\nCREATE TABLE #FileContent (Line NVARCHAR(4000));\n\n-- Read the file using xp_cmdshell and insert the content into the table\nINSERT INTO #FileContent\nEXEC xp_cmdshell 'type C:\path\to\your\file.txt';\n\n-- Select the content from the temporary table\nSELECT * FROM #FileContent;\n\n-- Clean up\nDROP TABLE #FileContent;