SELECT TOP 50 \n    77 AS Repository, \n    DocID, \n    LastFileDate, \n    ImageCount, \n    LockedUserID, \n    DocClass, \n    Offline, \n    FileType, \n    OrigFileName, \n    C_CredCardSSN, \n    C_CredCardName,\n    C_CredCardDocType, \n    C_CredCardDocDate, \n    C_CredCardStatus \nFROM \n    D77 \nWHERE \n    C_CredCardSSN LIKE @sp1\nORDER BY \n    C_CredCardSSN, \n    C_CredCardName