ALTER PROCEDURE dbo.spSelectBenchEvent\n(\n    @EVENT_DATE DATETIME\n)\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    SELECT ID, EID, BENCHID, PACCODE, START_TIME, END_TIME\n    FROM tbl_benchbook\n    WHERE \n        YEAR(START_TIME) = YEAR(@EVENT_DATE) AND\n        MONTH(START_TIME) = MONTH(@EVENT_DATE) AND\n        DAY(START_TIME) = DAY(@EVENT_DATE)\n    ORDER BY START_TIME;\nEND