-- Get information about database users\nSELECT * FROM sys.database_principals WHERE type IN ('S', 'U', 'G', 'K', 'R', 'C');\n\n-- Get information about database roles\nSELECT * FROM sys.database_principals WHERE type = 'R';\n\n-- Get information about role memberships\nSELECT r.name AS RoleName, m.name AS MemberName\nFROM sys.database_role_members rm\nJOIN sys.database_principals r ON rm.role_principal_id = r.principal_id\nJOIN sys.database_principals m ON rm.member_principal_id = m.principal_id;\n\n-- Get information about object permissions\nSELECT \n    dp.name AS PrincipalName,\n    o.name AS ObjectName,\n    p.permission_name AS Permission,\n    p.state_desc AS PermissionState\nFROM sys.database_permissions p\nJOIN sys.objects o ON o.object_id = p.major_id\nJOIN sys.database_principals dp ON dp.principal_id = p.grantee_principal_id;