SELECT \n    p.ProductId, \n    p.ProductTitle, \n    p.Price, \n    pil.LookUpId, \n    pil.FileID, \n    pil.Order\nFROM \n    Products p\nJOIN \n    ProductImagesLookUp pil ON p.ProductId = pil.ProductId\nJOIN \n    Images i ON pil.FileID = i.FileID\nWHERE \n    pil.Order = (\n        SELECT MIN(pil2.Order)\n        FROM ProductImagesLookUp pil2\n        WHERE pil2.ProductId = p.ProductId\n    )\nORDER BY \n    p.ProductId;