DELIMITER //\n\nCREATE PROCEDURE find_missing_blocks()\nBEGIN\n    SELECT\n        t1.end_block + 1 AS missing_start_block,\n        t2.start_block - 1 AS missing_end_block\n    FROM\n        your_table t1\n    JOIN\n        your_table t2 ON t1.end_block + 1 < t2.start_block\n    WHERE\n        NOT EXISTS (\n            SELECT 1\n            FROM your_table t3\n            WHERE t3.start_block = t1.end_block + 1\n        )\n    ORDER BY\n        t1.id;\nEND //\n\nDELIMITER ;