CREATE TRIGGER trg_CheckEmployeeCustomerAssociation\nON WorkItem\nAFTER INSERT, UPDATE\nAS\nBEGIN\n    -- Check for invalid Employee-Customer associations\n    IF EXISTS (\n        SELECT 1\n        FROM inserted i\n        JOIN Employee e ON i.EmployeeId = e.EmployeeId\n        WHERE i.CustomerId <> e.CustomerId\n    )\n    BEGIN\n        -- If any invalid association is found, raise an error and rollback the transaction\n        RAISERROR('The Employee is not associated with the specified Customer.', 16, 1);\n        ROLLBACK TRANSACTION;\n    END\nEND