CASE\n    WHEN str_col = '0'\n         OR str_col = '00'\n         OR str_col = '000'\n         OR str_col = '0000'\n         OR str_col = '00000'\n         OR str_col = '000000'\n         OR str_col = '0000000'\n         OR str_col = '00000000'\n         OR str_col = '000000000'\n         OR str_col = '0000000000' THEN '0'\n    ELSE SUBSTRING(str_col, PATINDEX('%[^0]%', str_col + '1'), LEN(str_col))\nEND