-- Convert to UTC before inserting\nINSERT INTO "test" ("username", "dob") VALUES ('James T Kirk', '1945-01-01 11:30:11+05:30'::timestamptz AT TIME ZONE 'UTC');\nINSERT INTO "test" ("username", "dob") VALUES ('Spock', '1941-01-01 11:30:11+05:30'::timestamptz AT TIME ZONE 'UTC');\n\n-- Convert back to local time zone when querying\nSELECT username, dob AT TIME ZONE 'UTC' AT TIME ZONE 'Asia/Kolkata' AS dob FROM test WHERE username='James T Kirk';\nSELECT username, dob AT TIME ZONE 'UTC' AT TIME ZONE 'Asia/Kolkata' AS dob FROM test WHERE username='Spock';