CREATE TABLE AuditLog (\n    AuditID INT IDENTITY(1,1) PRIMARY KEY,\n    DeletedID UNIQUEIDENTIFIER,\n    DeletedBy NVARCHAR(128),\n    DeletedDate DATETIME\n);\n\nCREATE TRIGGER trgAfterDelete\nON YourTable\nAFTER DELETE\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    INSERT INTO AuditLog (DeletedID, DeletedBy, DeletedDate)\n    SELECT deleted.ID, ORIGINAL_LOGIN(), GETDATE()\n    FROM deleted;\n\n    SET NOCOUNT OFF;\nEND;