CREATE OR REPLACE FUNCTION SPLIT(DELIMITER CHAR(1), LIST VARCHAR(4000))\nRETURNS TABLE (ITEM VARCHAR(100))\nRETURN\n  WITH CTE (ITEM, REST) AS (\n    SELECT\n      CASE\n        WHEN LOCATE(DELIMITER, LIST) = 0 THEN LIST\n        ELSE LEFT(LIST, LOCATE(DELIMITER, LIST) - 1)\n      END,\n      CASE\n        WHEN LOCATE(DELIMITER, LIST) = 0 THEN ''\n        ELSE SUBSTR(LIST, LOCATE(DELIMITER, LIST) + 1)\n      END\n    FROM SYSIBM.SYSDUMMY1\n    UNION ALL\n    SELECT\n      CASE\n        WHEN LOCATE(DELIMITER, REST) = 0 THEN REST\n        ELSE LEFT(REST, LOCATE(DELIMITER, REST) - 1)\n      END,\n      CASE\n        WHEN LOCATE(DELIMITER, REST) = 0 THEN ''\n        ELSE SUBSTR(REST, LOCATE(DELIMITER, REST) + 1)\n      END\n    FROM CTE\n    WHERE REST <> ''\n  )\n  SELECT ITEM FROM CTE;