SELECT A.readings_miu_id, A.ReadDate, A.ReadTime, A.RSSI, A.Firmware, A.Active, A.OriginCol, A.ColID, A.Ownage, A.SiteID, A.PremID, A.prem_group1, A.prem_group2  \nINTO analyzedCopy2  \nFROM analyzedCopy AS A\nINNER JOIN \n    (SELECT readings_miu_id, ReadDate, MIN(ReadTime) AS MinReadTime\n     FROM analyzedCopy\n     GROUP BY readings_miu_id, ReadDate) AS B\nON A.readings_miu_id = B.readings_miu_id\n   AND A.ReadDate = B.ReadDate\n   AND A.ReadTime = B.MinReadTime\nORDER BY A.readings_miu_id, A.ReadDate;