-- Connect to SQL*Plus\nsqlplus your_user/your_password@your_db\n\n-- Create a directory object\nCREATE DIRECTORY backup_dir AS '/path/to/backup/location';\n\n-- Grant privileges\nGRANT READ, WRITE ON DIRECTORY backup_dir TO your_user;\n\n-- Run the Data Pump Export\nHOST expdp your_user/your_password@your_db schemas=your_schema directory=backup_dir dumpfile=backup.dmp logfile=backup.log;\n\n-- Exit SQL*Plus\nEXIT;