CREATE OR REPLACE TRIGGER audit_trigger\nAFTER INSERT OR UPDATE OR DELETE ON your_table\nFOR EACH ROW\nDECLARE\n  op_type CHAR(1);\nBEGIN\n  IF INSERTING THEN\n    op_type := 'I';\n    INSERT INTO audit_table (operation, col1, col2, col3, ...)\n    VALUES (op_type, :NEW.col1, :NEW.col2, :NEW.col3, ...);\n  \n  ELSIF UPDATING THEN\n    op_type := 'U';\n    INSERT INTO audit_table (operation, col1, col2, col3, ...)\n    VALUES (op_type, :NEW.col1, :NEW.col2, :NEW.col3, ...);\n  \n  ELSIF DELETING THEN\n    op_type := 'D';\n    INSERT INTO audit_table (operation, col1, col2, col3, ...)\n    VALUES (op_type, :OLD.col1, :OLD.col2, :OLD.col3, ...);\n  END IF;\nEND;\n/