CREATE TABLE Client (\n    ClientID INT PRIMARY KEY,\n    ClientName NVARCHAR(100)\n);\n\nCREATE TABLE Switch (\n    SwitchID INT PRIMARY KEY,\n    SwitchName NVARCHAR(100)\n);\n\nCREATE TABLE ClientSwitch (\n    ClientID INT,\n    SwitchID INT,\n    IsEnabled BIT,\n    PRIMARY KEY (ClientID, SwitchID),\n    FOREIGN KEY (ClientID) REFERENCES Client(ClientID),\n    FOREIGN KEY (SwitchID) REFERENCES Switch(SwitchID)\n);