CREATE OR REPLACE TRIGGER your_table_update_audit\nAFTER UPDATE ON your_table\nFOR EACH ROW\nDECLARE\n    v_sql_text CLOB;\nBEGIN\n    -- Get the SQL statement that triggered the update\n    FOR v_rec IN (SELECT sql_text FROM v$sqlarea WHERE sql_id = SYS_CONTEXT('USERENV', 'SESSIONID'))\n    LOOP\n        v_sql_text := v_rec.sql_text;\n    END LOOP;\n\n    -- Insert a record into the audit table\n    INSERT INTO update_audit (\n        audit_id,\n        table_name,\n        row_id,\n        user_name,\n        update_time,\n        sql_text\n    ) VALUES (\n        update_audit_seq.NEXTVAL,\n        'your_table',\n        :OLD.ROWID,\n        SYS_CONTEXT('USERENV', 'SESSION_USER'),\n        SYSTIMESTAMP,\n        v_sql_text\n    );\nEND;\n/