CREATE TABLE data_log (\n    id NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,\n    timestamp DATE NOT NULL,\n    data_value NUMBER NOT NULL\n)\nPARTITION BY RANGE (timestamp) (\n    PARTITION p0 VALUES LESS THAN (TO_DATE('2023-01-01', 'YYYY-MM-DD')),\n    PARTITION p1 VALUES LESS THAN (TO_DATE('2023-02-01', 'YYYY-MM-DD')),\n    PARTITION p2 VALUES LESS THAN (TO_DATE('2023-03-01', 'YYYY-MM-DD')),\n    -- Add more partitions as needed\n);\n\nCREATE INDEX idx_data_log_timestamp ON data_log (timestamp);