DECLARE @prefix NVARCHAR(100) = 'your_prefix'\n\n-- Find Tables and Views\nSELECT 'Table' AS ObjectType, TABLE_NAME AS ObjectName\nFROM INFORMATION_SCHEMA.TABLES\nWHERE TABLE_NAME LIKE @prefix + '%'\nUNION ALL\n-- Find Columns\nSELECT 'Column' AS ObjectType, COLUMN_NAME AS ObjectName\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE COLUMN_NAME LIKE @prefix + '%'\nUNION ALL\n-- Find Indexes\nSELECT 'Index' AS ObjectType, name AS ObjectName\nFROM sys.indexes\nWHERE name LIKE @prefix + '%'\nUNION ALL\n-- Find Functions\nSELECT 'Function' AS ObjectType, ROUTINE_NAME AS ObjectName\nFROM INFORMATION_SCHEMA.ROUTINES\nWHERE ROUTINE_NAME LIKE @prefix + '%' AND ROUTINE_TYPE = 'FUNCTION'\nUNION ALL\n-- Find Constraints\nSELECT 'Constraint' AS ObjectType, CONSTRAINT_NAME AS ObjectName\nFROM INFORMATION_SCHEMA.TABLE_CONSTRAINTS\nWHERE CONSTRAINT_NAME LIKE @prefix + '%'\nORDER BY ObjectType, ObjectName