-- Assume your tables are named Users, Assignments, and CourseTracking\n-- and you have a specific CourseID you want to assign to each user.\n\nDECLARE @CourseID INT;\nSET @CourseID = 1234; -- replace with your actual CourseID\n\n-- Insert into Assignments table\nINSERT INTO Assignments (UserID, CourseID, AssignmentDate)\nSELECT UserID, @CourseID, GETDATE()\nFROM Users;\n\n-- Insert into CourseTracking table\nINSERT INTO CourseTracking (UserID, CourseID, TrackingDate)\nSELECT UserID, @CourseID, GETDATE()\nFROM Users;