SELECT \n    s.record_id,\n    s.sales_data,\n    GROUP_CONCAT(CONCAT('Additional detail #', d.detail_id, ' for record ', s.record_id, ': ', d.detail) SEPARATOR '; ') AS additional_details\nFROM \n    sales_table s\nLEFT JOIN \n    additional_details_table d\nON \n    s.record_id = d.record_id\nGROUP BY \n    s.record_id, s.sales_data;