SELECT \n    s.record_id,\n    s.sales_data,\n    STUFF(\n        (SELECT '; ' + 'Additional detail #' + CAST(d.detail_id AS VARCHAR) + ' for record ' + CAST(s.record_id AS VARCHAR) + ': ' + d.detail\n         FROM additional_details_table d\n         WHERE d.record_id = s.record_id\n         FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), \n        1, 2, ''\n    ) AS additional_details\nFROM \n    sales_table s\nLEFT JOIN \n    additional_details_table d\nON \n    s.record_id = d.record_id\nGROUP BY \n    s.record_id, s.sales_data;