DECLARE\n  year_value NUMBER;\n  column_name VARCHAR2(20);\n  sql_stmt VARCHAR2(1000);\nBEGIN\n  -- Assume we retrieve the year value from some table\n  SELECT year INTO year_value FROM your_table WHERE ROWNUM = 1;\n  \n  -- Determine the column name based on the year value\n  IF year_value < 1950 THEN\n    column_name := 'OldYear';\n  ELSE\n    column_name := 'NewYear';\n  END IF;\n  \n  -- Construct the dynamic SQL statement\n  sql_stmt := 'SELECT year AS ' || column_name || ' FROM your_table';\n  \n  -- Execute the dynamic SQL statement\n  EXECUTE IMMEDIATE sql_stmt;\nEND;\n/