CREATE TABLE base_actor (\n    id SERIAL PRIMARY KEY,\n    name VARCHAR(100),\n    -- other common fields\n);\n\nCREATE TABLE worker (\n    id SERIAL PRIMARY KEY,\n    base_actor_id INT REFERENCES base_actor(id),\n    worker_specific_field VARCHAR(100)\n);\n\nCREATE TABLE employer (\n    id SERIAL PRIMARY KEY,\n    base_actor_id INT REFERENCES base_actor(id),\n    employer_specific_field VARCHAR(100)\n);