USE [Skunkworks]\nGO\n\nSELECT \n    outerCall.CallGUID AS OuterCallGUID,\n    innerCall.CallGUID AS InnerCallGUID,\n    innerCall.StartedOn,\n    innerCall.FinishedOn\nFROM \n    [dbo].[ClassMemberCall] AS outerCall\nJOIN \n    [dbo].[ClassMemberCall] AS innerCall\n    ON outerCall.InstanceGUID = innerCall.InstanceGUID\n    AND innerCall.StartedOn >= outerCall.StartedOn\n    AND innerCall.FinishedOn <= outerCall.FinishedOn\nWHERE \n    outerCall.CallGUID = '1F45C171-910B-4F17-9286-3D8CDEB7A0C4'