CREATE TABLE materialized_view AS\n   SELECT n.node_ID, n.node_title, n.node_lastupdated, vp.viewpermission_usergroupID\n   FROM node n\n   INNER JOIN viewpermission vp ON n.node_ID = vp.viewpermission_nodeID;\n   \n   -- Index for the materialized view\n   CREATE INDEX mv_idx ON materialized_view (viewpermission_usergroupID, node_lastupdated);\n   \n   -- Query using the materialized view\n   SELECT node_ID, node_title, node_lastupdated\n   FROM materialized_view\n   WHERE viewpermission_usergroupID IN (<...usergroups of current user...>)\n   ORDER BY node_lastupdated DESC\n   LIMIT 20;