declare @O table (\n    Month datetime,\n    ACount int NULL\n)\n\ninsert into @O values ('2009-01-01', 0)\ninsert into @O values ('2009-01-01', 1)\ninsert into @O values ('2008-01-01', 1)\ninsert into @O values ('2007-01-01', 0)\n\n;with CTE as (\n    select o1.Month, o1.ACount\n    from @O o1\n    where o1.ACount = 0\n      and exists (select 1 from @O o2 where o1.Month = o2.Month and o2.ACount > 0)\n)\ndelete from @O\nwhere Month in (select Month from CTE)\n  and ACount = 0