DECLARE @table_name nvarchar(128);\nDECLARE @column_name nvarchar(128);\nDECLARE @sql nvarchar(max);\n\nDECLARE columns_cursor CURSOR FOR\nSELECT t.name AS table_name, c.name AS column_name\nFROM sys.tables AS t\nINNER JOIN sys.columns AS c ON t.object_id = c.object_id\nINNER JOIN sys.types AS ty ON c.user_type_id = ty.user_type_id\nWHERE ty.name = 'datetime';\n\nOPEN columns_cursor;\n\nFETCH NEXT FROM columns_cursor INTO @table_name, @column_name;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql = 'UPDATE ' + QUOTENAME(@table_name) + ' SET ' + QUOTENAME(@column_name) + ' = DATEADD(YEAR, 1, ' + QUOTENAME(@column_name) + ');';\n    EXEC sp_executesql @sql;\n    \n    FETCH NEXT FROM columns_cursor INTO @table_name, @column_name;\nEND\n\nCLOSE columns_cursor;\nDEALLOCATE columns_cursor;