DECLARE\n  polygon SDO_GEOMETRY;\n  centroid_point SDO_GEOMETRY;\n  valid_point SDO_GEOMETRY;\nBEGIN\n  -- Substitute your polygon geometry here\n  polygon := SDO_GEOMETRY(...);\n\n  -- Get the centroid of the polygon\n  centroid_point := SDO_GEOM.SDO_CENTROID(polygon, 0.005);\n\n  -- Check if the centroid is within the polygon\n  IF SDO_GEOM.SDO_INSIDE(centroid_point, polygon) = 'TRUE' THEN\n    valid_point := centroid_point;\n  ELSE\n    -- If the centroid is not inside, use SDO_POINTONSURFACE\n    valid_point := SDO_GEOM.SDO_POINTONSURFACE(polygon, 0.005);\n  END IF;\n\n  -- Output the valid point\n  DBMS_OUTPUT.PUT_LINE('Valid point: ' || SDO_UTIL.TO_WKTGEOMETRY(valid_point));\nEND;\n/